import React, { useMemo, useState } from 'react'

import {useFilters, useSortBy, useTable, useGlobalFilter} from 'react-table'

import { Table, Form } from 'react-bootstrap'
import '../styles/Tabela.css'

function defaultColumnFilter({
    column: {filterValue, preFilteredRows, setFilter},
}) {
    const count = preFilteredRows.length

    return (
        <input 
            value={filterValue || ''}
            onChange={e => {
                setFilter(e.target.value || undefined)
            }}
            />
    )
}

export default function Tabela({ columns, data }) {
    const [filterInput, setFilterInput] = useState('');

    const handleFilterChange = e => {
        const value = e.target.value;
        setFilter('first_name', value);
        setFilterInput(value);
    }

    const defaultColumn = useMemo(
        () => ({
          // Let's set up our default Filter UI
          Filter: defaultColumnFilter,
        }),
        []
      )


    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
        setFilter
    } = useTable({
        columns,
        data,
        defaultColumn,
        filterTypes
    },
        useFilters,
        useSortBy
    )
    return(
        <div className="tabela">
            <Table {...getTableProps} striped hover bordered size="sm">
                <thead>
                    {headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                                <th {...column.getHeaderProps()}>{column.render("Header")}</th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {rows.map((row, i) => {
                        prepareRow(row)
                        return (
                            <tr {...row.getRowProps()}>
                                {row.cells.map(cell => {
                                    return <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                                })}
                            </tr>
                        )
                    })}
                </tbody>
            </Table>
            <Form.Control  className='search-bar' type="text" value={filterInput} onChange={handleFilterChange} placeholder={'Search name'} />
            <Form.Control  className='search-bar' type="text" value={filterInput} onChange={handleFilterChange} placeholder={'Search name'} />
            <Form.Control  className='search-bar' type="text" value={filterInput} onChange={handleFilterChange} placeholder={'Search name'} />
            <Form.Control  className='search-bar' type="text" value={filterInput} onChange={handleFilterChange} placeholder={'Search name'} />
            <Form.Control  className='search-bar' type="text" value={filterInput} onChange={handleFilterChange} placeholder={'Search name'} />
            <Form.Control  className='search-bar' type="text" value={filterInput} onChange={handleFilterChange} placeholder={'Search name'} />
            <Form.Control  className='search-bar' type="text" value={filterInput} onChange={handleFilterChange} placeholder={'Search name'} />
            <Form.Control  className='search-bar' type="text" value={filterInput} onChange={handleFilterChange} placeholder={'Search name'} />
        </div>

       
    )

}
