import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import "../../styles/Page.css";
import "../../styles/EditContact.css";

import isEqual from "lodash.isequal";
import { Form, Button } from "react-bootstrap";
import { DateTime } from "luxon";

const EditContact = ({ api, setContacts, contacts, stages }) => {
  const [contact, setContact] = useState();
  const { id } = useParams();

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [title, setTitle] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [contactMethod, setContactMethod] = useState("");

  const [company, setCompany] = useState("");
  const [address, setAddress] = useState("");
  const [address2, setAddress2] = useState("");
  const [city, setCity] = useState("");
  const [zipCode, setZipCode] = useState("");
  const [websiteUrl, setWebsiteUrl] = useState("");

  const [stage, setStage] = useState("");
  const [dealSize, setDealSize] = useState("");
  const [followUpDate, setFollowUpDate] = useState("");
  const [notes, setNotes] = useState("");

  useEffect(() => {
    const apiToken = window.localStorage.getItem("api_token");
    api
      .get(`/contacts/${id}`, { headers: { token: apiToken } })
      .then((res) => {
        if (!isEqual(res.data, contact)) {
          setContact(res.data);
          fillForm(res.data);
        }
      })
      .catch((error) => {
        console.log(error.response.data.message);
      });
  }, [api, contact, followUpDate, id]);

  const fillForm = (data) => {
    console.log(data);
    setFirstName(data.first_name);
    setLastName(data.last_name);
    setTitle(data.title);
    setPhone(data.phone);
    setEmail(data.email);
    setContactMethod(data.contact_method);

    setCompany(data.company);
    setAddress(data.address);
    setAddress2(data.address2);
    setCity(data.city);
    setZipCode(data.zip_code);
    setWebsiteUrl(data.website_url);

    setStage(data.stage_id);
    setDealSize(data.deal_size);
    setFollowUpDate(
      DateTime.fromFormat(data.follow_up_date, "yyyy-MM-dd HH:mm:ss").toFormat(
        "yyyy-MM-dd"
      )
    );
    setNotes(data.notes);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    console.log(followUpDate);
    console.log(contactMethod);
    const apiToken = window.localStorage.getItem("api_token");

    api
      .put(
        `/contacts/${id}`,
        {
          firstName,
          lastName,
          title,
          phone,
          contactMethod,
          company,
          address,
          address2,
          city,
          zipCode,
          websiteUrl,
          stage,
          dealSize,
          followUpDate,
          notes,
        },
        { headers: { token: apiToken } }
      )
      .then((res) => {
        console.log(res.data);
        // setRole(res.data.role);
        // history.push("/Dashboard");
      })
      .catch((error) => {
        alert(error.response.data.message);
      });
  };

  // console.log(contact);
  return (
    <div className="container">
      <h5>Edit Lead:</h5>
      <div className="form-container">
        <Form>
          <hr />
          <div className="input-container">
            <p>First name:</p>
            <Form.Control
              type="text"
              name="first-name"
              value={firstName}
              onChange={(e) => {
                setFirstName(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Last name:</p>
            <Form.Control
              type="text"
              name="last-name"
              value={lastName}
              onChange={(e) => {
                setLastName(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Title:</p>
            <Form.Control
              type="text"
              name="title"
              value={title}
              onChange={(e) => {
                setTitle(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Phone:</p>
            <Form.Control
              type="text"
              name="phone"
              value={phone}
              onChange={(e) => {
                setPhone(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Email:</p>
            <Form.Control
              type="text"
              name="email"
              value={email}
              onChange={(e) => {
                setEmail(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Contact method:</p>
            <Form.Control
              as="select"
              name="contact-method"
              value={contactMethod}
              onChange={(e) => {
                setContactMethod(e.target.value);
              }}
            >
              <option value="">Select contact method</option>
              <option value="phone">Phone</option>
              <option value="email">Email</option>
            </Form.Control>
          </div>

          <h5>Company details: </h5>
          <hr />
          <div className="input-container">
            <p>Company:</p>
            <Form.Control
              type="text"
              name="company"
              value={company}
              onChange={(e) => {
                setCompany(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Address:</p>
            <Form.Control
              type="text"
              name="address"
              value={address}
              onChange={(e) => {
                setAddress(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Address 2:</p>
            <Form.Control
              type="text"
              name="address2"
              value={address2}
              onChange={(e) => {
                setAddress2(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>City:</p>
            <Form.Control
              type="text"
              name="city"
              value={city}
              onChange={(e) => {
                setCity(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Zip code:</p>
            <Form.Control
              type="text"
              name="zip-code"
              value={zipCode}
              onChange={(e) => {
                setZipCode(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Website url:</p>
            <Form.Control
              type="text"
              name="website-url"
              value={websiteUrl}
              onChange={(e) => {
                setWebsiteUrl(e.target.value);
              }}
            />
          </div>

          <hr />
          <div className="input-container">
            <p>Stage:</p>
            <Form.Control
              as="select"
              name="stage"
              value={stage}
              onChange={(e) => {
                setStage(e.target.value);
              }}
            >
              <option value="">Select a stage</option>
              {stages.map((stage) => (
                <option key={stage.id} value={stage.id}>
                  {stage.stage}
                </option>
              ))}
            </Form.Control>
          </div>
          <div className="input-container">
            <p>Deal size:</p>
            <Form.Control
              type="text"
              name="deal-size"
              value={dealSize}
              onChange={(e) => {
                setDealSize(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Follow Up Date:</p>
            <Form.Control
              type="date"
              name="follow-up-date"
              value={followUpDate}
              onChange={(e) => {
                setFollowUpDate(e.target.value);
              }}
            />
          </div>
          <div className="input-container notes-textarea">
            <p>Notes:</p>
            <Form.Control
              as="textarea"
              name="notes"
              rows={10}
              value={notes}
              onChange={(e) => {
                setNotes(e.target.value);
              }}
            />
          </div>
          <Button type="submit" onClick={onSubmit}>
            Accept
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default EditContact;
