import React, { useMemo, useState } from "react";

import { useFilters, useSortBy, useTable, useGlobalFilter } from "react-table";

import { Table, Form } from "react-bootstrap";
import "../styles/Tabela.css";

import { numberFormat } from "./NumberFormat";

export default function Tabela({ columns, data }) {
  const [filterInput, setFilterInput] = useState("");

  const handleFilterChange = (e) => {
    const value = e.target.value;
    setFilter("first_name", value);
    setFilterInput(value);
  };

  let totalDealSize = 0;

  let stageRow = <tr></tr>;

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    setFilter,
  } = useTable(
    {
      columns,
      data,
    },
    useFilters,
    useSortBy
  );
  return (
    <div className="tabela">
      <Table {...getTableProps} hover bordered size="sm">
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                  {column.render("Header")}
                  <span>
                    {column.isSorted ? (column.isSortedDesc ? "ðŸ”½" : "ðŸ”¼") : ""}
                  </span>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {/* <tr>
            <td colspan="11">Prospect</td>
          </tr> */}
          {rows.map((row, i) => {
            prepareRow(row);
            //console.log(row.allCells[0].value);
            totalDealSize += row.allCells[8].value;
            if (row.allCells[0].value === "1. Prospect (10%)") {
              console.log("header 1");
              stageRow = (
                <tr>
                  <td colspan="11">1. Prospect (10%)</td>
                </tr>
              );
            }
            return (
              <tr {...row.getRowProps()} key={i}>
                {row.cells.map((cell) => {
                  return (
                    <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                  );
                })}
              </tr>
            );
          })}
        </tbody>
        <tfoot>
          <tr>
            {headerGroups.map((headerGroup) =>
              headerGroup.headers.map((column, i) => {
                if (i === 0) {
                  return <td colspan="8">Total:</td>;
                }
                if (i === 8) {
                  return <td colspan="3">{numberFormat(totalDealSize)}</td>;
                }
              })
            )}
            {/* <td></td>
            <td></td>
            <td>Sum</td>
            <td>Suma</td>
            <td></td> */}
          </tr>
        </tfoot>
      </Table>
      <Table></Table>
      <h5>Search Contacts</h5>
      <div className="form-container">
        <Form>
          {headerGroups.map((headerGroup) =>
            headerGroup.headers.map((column) =>
              column.canFilter ? column.render("Filter") : null
            )
          )}
        </Form>
      </div>
    </div>
  );
}
