import React, { useMemo } from 'react'
import { Form } from 'react-bootstrap'

const SelectColumnFilter = ({column}) => {
    const {filterValue, setFilter} = column
    // const options = useMemo(() => {
    //     const options = new Set()
    //     preFilteredRows.forEach(row => {
    //         options.add(row.values[id])
    //     })
        
    // }, [id, preFilteredRows])

    return (
        <div>
            <Form.Control 
                as="select" 
                name="stage"
                value={filterValue} onChange={(e) => {setFilter(e.target.value || undefined)}}
                
            >   
                <option value=''>-Select a stage-</option>
                   {/* {stages.map((stage, i) => (
                        <option key={i} value={stage.stage}>{stage.stage}</option>
                    ))}
                    {console.log(stages)} */}
                    <option key='1' value='1. Prospect (10%)'>1. Prospect (10%)</option>
                    <option key='2' value='2. Forecast (50%)'>2. Forecast (50%)</option>
                    <option key='3' value='3. Prospect (80%)'>3. Prospect (80%)</option>
                    <option key='4' value='4. Win/Closed (100%)'>4. Win/Closed (100%)</option>
            </Form.Control>
        </div>
    )
}

export default SelectColumnFilter
