import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { Button } from "react-bootstrap";
import "../styles/Header.css";
import axios from "axios";
import { isEqual } from "lodash";

function Header({ userType = "", setRole, setUserType }) {
  const history = useHistory();
  const [australiaTime, setAustraliaTime] = useState("");

  useEffect(() => {
    axios
      .get(
        `https://cors-anywhere.herokuapp.com/http://worldtimeapi.org/api/Australia/Brisbane`
        // {
        //   headers: { "Access-Control-Allow-Origin": "*" },
        // }
      )
      .then((res) => {
        if (!isEqual(australiaTime, res.data)) {
          setAustraliaTime(res.data);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, [australiaTime]);

  console.log(australiaTime);

  const logOut = () => {
    window.localStorage.removeItem("api_token");
    window.localStorage.removeItem("role");
    window.localStorage.removeItem("staff_id");
    setRole("");
    setUserType("");
    history.push("/Login");
  };

  let logoutButton;
  if (userType !== "") {
    logoutButton = (
      <Button variant="link" onClick={logOut} className="logout-button">
        Logout
      </Button>
    );
  }

  return (
    <>
      <nav className="header">
        <div className="header-container">
          <h1 className="header-logo">ITEH CRM 2021</h1>
          <ul>
            <li className="header-item-view">{userType}</li>
            <li className="header-item-logout">{logoutButton}</li>
          </ul>
        </div>
      </nav>
    </>
  );
}

export default Header;
