import React, { useEffect, useState } from "react";
import { useParams } from "react-router";
import "../../styles/Page.css";
import isEqual from "lodash.isequal";
import { Form, Button } from "react-bootstrap";

const EditContact = ({ api, setContacts, contacts, stages }) => {
  const [contact, setContact] = useState();
  const { id } = useParams();

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [contactMethod, setContactMethod] = useState("");
  const [stage, setStage] = useState("");
  const [company, setCompany] = useState("");
  const [followUpDate, setFollowUpDate] = useState("");

  useEffect(() => {
    const apiToken = window.localStorage.getItem("api_token");
    api
      .get(`/contacts/${id}`, { headers: { token: apiToken } })
      .then((res) => {
        if (!isEqual(res.data, contact)) {
          setContact(res.data);
          fillForm(res.data);
        }
      })
      .catch((error) => {
        console.log(error.response.data.message);
      });
  }, [api, contact, id]);

  const fillForm = (data) => {
    console.log(data);
    setFirstName(data.first_name);
    setLastName(data.last_name);
    setPhone(data.phone);
    setEmail(data.email);
    setStage(data.stage_id);
    setCompany(data.company);
    setFollowUpDate(data.follow_up_date);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    console.log(firstName);
  };

  const onClear = (e) => {
    e.preventDefault();
    console.log(firstName);
  };

  // console.log(contact);
  return (
    <div className="container">
      <h5>Edit Lead:</h5>
      <div className="form-container">
        <Form>
          <div className="input-container">
            <p>First name:</p>
            <Form.Control
              type="text"
              name="first-name"
              value={firstName}
              onChange={(e) => {
                setFirstName(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Last name:</p>
            <Form.Control
              type="text"
              name="last-name"
              value={lastName}
              onChange={(e) => {
                setLastName(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Phone:</p>
            <Form.Control
              type="text"
              name="phone"
              value={phone}
              onChange={(e) => {
                setPhone(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Email:</p>
            <Form.Control
              type="text"
              name="email"
              value={email}
              onChange={(e) => {
                setEmail(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Contact method:</p>
            <Form.Control
              as="select"
              name="contact-method"
              value={contactMethod}
              onChange={(e) => {
                setContactMethod(e.target.value);
              }}
            />
            <option value="">Select contact method</option>
            <option value="phone">Phone</option>
            <option value="email">Emaiul</option>
          </div>

          <div className="input-container">
            <p>Stage:</p>
            <Form.Control
              as="select"
              name="stage"
              value={stage}
              onChange={(e) => {
                setStage(e.target.value);
              }}
            >
              <option value="">Select a stage</option>
              {stages.map((stage) => (
                <option key={stage.id} value={stage.id}>
                  {stage.stage}
                </option>
              ))}
            </Form.Control>
          </div>
          <div className="input-container">
            <p>Company:</p>
            <Form.Control
              type="text"
              name="company"
              value={company}
              onChange={(e) => {
                setCompany(e.target.value);
              }}
            />
          </div>
          <div className="input-container">
            <p>Follow Up Date:</p>
            <Form.Control
              type="date"
              name="follow-up-date"
              value={followUpDate}
              onChange={(e) => {
                setFollowUpDate(e.target.value);
              }}
            />
          </div>
          <Button type="submit" onClick={onSubmit}>
            Accept
          </Button>
          <Button type="clear" onClick={onClear}>
            Clear
          </Button>
        </Form>
      </div>
    </div>
  );
};

export default EditContact;
