import React from 'react'
import { Form } from 'react-bootstrap'

const TextColumnFilter = ({column}) => {
    const { filterValue, setFilter } = column

    const filterType = (text, select, date) => {
        if(text) {
            return <Form.Control value={filterValue || ''} onChange={(e => setFilter(e.target.value))} placeholder={column.render('Header')} className='search-bar' type="text"/>

        }
    }

    return (
        <span>
            {/* <Form.Control value={filterValue || ''} onChange={(e => setFilter(e.target.value))} placeholder={column.render('Header')} className='search-bar' type="text"/> */}
            {filterType(true, false, false)}
        </span>
    )
}

export default TextColumnFilter
