import React, { useEffect, useState} from 'react'
import { Link, useHistory } from 'react-router-dom';
import { Button } from 'react-bootstrap';
import '../styles/Header.css'

function Header() {
    // const whoIsLoggedIn = () => {
    //     var loggedIn = ''
    //     (role === null) ? '' : ((role === 'manager') ? loggedIn = 'Manager view' : loggedIn = 'Employee view'  )
    //     return loggedIn;
    // }
    const [whoIsLoggedIn, setWhoIsLoggedIn] = useState('')
    const [role, setRole] = useState('')
    const history = useHistory();

    const logOut = () => {
        window.localStorage.removeItem('api_token')
        window.localStorage.removeItem('role')
        history.push('/login')
    }

    useEffect(() => {
        const apiToken = window.localStorage.getItem('api_token')
        const role = window.localStorage.getItem('role')
        console.log(apiToken)
        if(apiToken) {
            setRole(role)
            if(role === 'manager') {
                setWhoIsLoggedIn('Manager view')
            }
        } else {
            setRole('')
        }
    },[role])
    return (
        <>
            <nav className="header">
                <div className="header-container">
                    <h1 className="header-logo">ITEH CRM 2021</h1>
                    <ul>
                        <li className="header-item-view">
                            {whoIsLoggedIn}
                        </li>
                        <li className="header-item-logout">
                            <Button onClick={logOut} className="logout-button">Logout</Button>
                        </li>
                    </ul>
                </div>
            </nav>
        </>
    )
}

export default Header
