import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import isEqual from "lodash.isequal";
import { numberFormat } from "../NumberFormat";
import {
  XYPlot,
  XAxis,
  YAxis,
  VerticalGridLines,
  HorizontalGridLines,
  VerticalBarSeries,
} from "react-vis";

import ManagerTable from "../ManagerTable";
import TextColumnFilter from "../TextColumnFilter";
import DateColumnFilter from "../DateColumnFilter";

const companyColumns = [
  {
    Header: "Name",
    accessor: "company",
    Filter: TextColumnFilter,
  },
  {
    Header: "Address",
    accessor: "address",
    Filter: TextColumnFilter,
  },
  {
    Header: "Address2",
    accessor: "address2",
    Filter: TextColumnFilter,
  },
  {
    Header: "Website",
    accessor: "website_url",
    Filter: TextColumnFilter,
  },
  {
    Header: "City",
    accessor: "company_city",
    Filter: TextColumnFilter,
  },
  {
    Header: "Company Deal Size",
    accessor: "company_deal_size",
    Cell: ({ value }) => {
      if (value !== undefined) return numberFormat(value);
      return "";
    },
    Filter: DateColumnFilter,
  },
  {
    Header: "",
    accessor: "id",
    Cell: ({ value }) => <Link to={`/EditCompany/${value}`}>Edit</Link>,
    disableSortBy: true,
    Filter: DateColumnFilter,
  },
];

const Companies = ({
  companies,
  setCompanies,
  staff,
  api,
  companiesDetailed,
  setCompaniesDetailed,
}) => {
  let table;

  table = (
    <ManagerTable
      columns={companyColumns}
      data={companiesDetailed}
    ></ManagerTable>
  );

  return (
    <div className="container">
      <div>
        <XYPlot
          margin={{ bottom: 70 }}
          xType="ordinal"
          width={300}
          height={300}
        >
          <VerticalGridLines />
          <HorizontalGridLines />
          <XAxis tickLabelAngle={-45} />
          <YAxis />
          <VerticalBarSeries
            data={[
              { x: "Apples", y: 10 },
              { x: "Bananas", y: 5 },
              { x: "Crainberries", y: 15 },
            ]}
          />
        </XYPlot>
      </div>
      {table}
    </div>
  );
};

export default Companies;
