import './App.css';
import React, { useState, useEffect, useMemo } from "react";
import axios from 'axios';
import Tabela from './Components/Tabela'
import { Badge } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.css';
import Header from './Components/Header';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'
import Navbar from './Components/Navbar';
import Login from './Components/Pages/Login'
import Dashboard from './Components/Pages/Dashboard'
import NewLead from './Components/Pages/NewLead'
import SearchLeads from './Components/Pages/SearchLeads'
import Home from './Components/Pages/Home'


const Genres = ({ values }) => {
  return (
    <>
      {values.map((genre, idx) => {
        return (
          <Badge variant="primary" key={idx}>
            {genre}
          </Badge>
        )
      })}
    </>
  )
}

function App() {
  const columns = useMemo(
    () => [
      {
        Header: "TV Show",
        columns: [
          {
            Header: "Name",
            accessor: "show.name"
          },
          {
          Header: 'Type',
          accessor: 'show.type'
          }
        ]
      },
      {
        Header: "Details",
        columns: [
          {
            Header: 'Language',
            accessor: 'show.language'
          },
          {
            Header: 'Genre(s)',
            accessor: 'show.genres',
            Cell: ({ cell: {value} }) => <Genres values={value} />
          },
          {
          Header: 'runtime',
          accessor: 'show.runtime',
          Cell: ({ cell: {value} }) => {
            const hour = Math.floor(value / 60)
            const min = Math.floor(value % 60)
            return (
              <>
                {hour > 0 ? `${hour} hr${hour > 1 ? "s" : ""}` : ""}
                {min > 0 ? `${min} min${min > 1 ? "s" : ""}` : ""}
              </>
            )
          }
          },
          {
            Header: "Status",
            accessor: "show.status"
          }
        ]
      }
    ],
    []
  )
  const [data, setData] = useState([]);

  useEffect(()=> {
    (async () => {
      const result = await axios("https://api.tvmaze.com/search/shows?q=snow");
      setData(result.data);
    })()
  }, [])
  return (
    <div className="App">
      <Router>
        <Header view="Empoloyee view" />
        <Navbar />
        <Switch>
          <Route path="/" exact={true} component="{Home}" />
          <Route path="/Login" exact={true} component="{Login}" />
          <Route path="/Dashboard" exact={true} component="{Dashboard}" />
          <Route path="/NewLead" exact={true} component="{NewLead}" />
          <Route path="/SearchLeads" exact={true} component="{SearchLeads}" />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
