import React, {useState, useEffect, useMemo} from 'react'
import '../../styles/Page.css'
import isEqual from 'lodash.isequal'
import Tabela from '../Tabela'
import ManagerTable from '../ManagerTable'


const employeeColumns = [
                {
                    Header: 'First name',
                    accessor: 'first_name'
                },
                {
                    Header: 'Last name',
                    accessor: 'last_name'
                },
                {
                    Header: 'Title',
                    accessor: 'title'
                },
                {
                    Header: 'Phone',
                    accessor: 'phone'
                },
                {
                    Header: 'Email',
                    accessor: 'email'
                },
                {
                    Header: 'Contact method',
                    accessor: 'contact_method'
                },
                {
                    Header: 'Company',
                    accessor: 'company'
                },
            ]

const managerColumns = [
        {
            Header: 'Name',
            accessor: 'name'
        },
        {
            Header: 'Email',
            accessor: 'email'
        },
        {
            Header: 'Role',
            accessor: 'role'
        },
        {
            Header: 'Status',
            accessor: 'status'
        },
    ]

//[
    // {
    //     Header: 'Leads',
    //     columns: 

const Dashboard = ({api, stage, setStage, contacts, setContacts, role, staff, setStaff}) => {

    useEffect(() => {
        if(role !== '') {
            const apiToken = window.localStorage.getItem('api_token')

            if(role !== 'manager') {
                 
                api.get(`/contacts`, {headers: {token: apiToken}}).then(res => {
                    if(!isEqual(res.data, contacts)) {
                        setContacts(res.data)
                    }        
                    }).catch(error => {
                        console.log(error.response.data.message)
                    })

                } else {
                    api.get(`/staff`, {headers: {token: apiToken}}).then(res => {
                        if(!isEqual(res.data, staff)) {
                            setStaff(res.data)
                        }        
                        }).catch(error => {
                            console.log(error.response.data.message)
                        })
                }
                api.get(`/stages`, {headers: {token: apiToken}}).then(res => {
                    if(!isEqual(res.data, stage)) {
                        setStage(res.data)
                    }        
                    }).catch(error => {
                        console.log(error.response.data.message)
                    })
                    
            }
    },[contacts, stage, staff])

 

    return (
        <div className='container'>
            {/* {(contacts.length) ? contacts[0].first_name : ''} */}
            <Tabela columns={managerColumns} data={staff} />
            <ManagerTable columns={managerColumns} data={staff}></ManagerTable>
        </div>
    )
}

export default Dashboard
